mapper.select:
select * from(
select
,
ROW_NUMBER() OVER(order by CT_TM) as ROW_NUM
from TL.tbl_rrs_req
where 1=1

    and REQ_ID=#{requestid}


    and CT_TM >= #{startTime}


    and CT_TM < #{endTime}


    and mobile = #{mobile}


    and RID = #{riskid}


    and PROD = #{productid}


    and biz = #{production}


    and usr_id = #{userid}


    and CRD_NO = #{bankcardno}


    and MEC_NO = #{merchantno}


    and PAY_RESULT ${payresult}


    and FRP_CODE = #{frpCode}


    and crd_typ = #{bankcardtype}


    and ID_NO = #{idNo}


    and TERMINAL_ID = #{terminalId}

and RETCODE in('6102','6103','6104','6105','6106','6101','6111')
) as a where a.ROW_NUM > #{startNo} and a.ROW_NUM <= #{endNo}
FETCH FIRST ${pageRow} ROWS  ONLY
with ur

 select
		count(1)
 from TL.tbl_rrs_req
 where 1=1

     and REQ_ID=#{requestid}


     and CT_TM >= #{startTime}


     and CT_TM < #{endTime}


     and mobile = #{mobile}


     and RID = #{riskid}


     and PROD = #{productid}


     and biz = #{production}


     and usr_id = #{userid}


     and CRD_NO = #{bankcardno}


     and MEC_NO = #{merchantno}


     and PAY_RESULT ${payresult}


     and FRP_CODE = #{frpCode}


     and crd_typ = #{bankcardtype}


     and ID_NO = #{idNo}


     and TERMINAL_ID = #{terminalId}

  and RETCODE in('6102','6103','6104','6105','6106','6101','6111')
 with ur
